let title=document.title.split("/")[1],links=document.querySelectorAll("header .nav-link");if(links.forEach(e=>e.innerHTML===title&&e.classList.add("active")),"FoadRoad/Service"==document.title){document.querySelectorAll(".service-frequently .accordion-item button").forEach(e=>{e.addEventListener("click",()=>{e.classList.contains("collapsed")?(e.firstElementChild.classList.remove("fa-minus"),e.firstElementChild.classList.add("fa-plus")):(e.firstElementChild.classList.add("fa-minus"),e.firstElementChild.classList.remove("fa-plus"))})})}if("FoadRoad/Resturant"==document.title){let e=document.querySelector(".resturant-find input"),t=document.querySelector(".resturant-find button"),l=document.querySelectorAll(".resturant-resturants .card");t.addEventListener("click",t=>{t.preventDefault(),l.forEach(t=>{t.lastElementChild.firstElementChild.innerHTML.toLowerCase().includes(e.value.toLowerCase().trim())?t.classList.remove("d-none"):t.classList.add("d-none")})});let r=document.querySelectorAll(".resturant-resturants .filter .filter-result");r.forEach(e=>{e.addEventListener("click",()=>{e.classList.toggle("active"),r.forEach(t=>t===e||t.classList.remove("active")),l.forEach(t=>{t.firstElementChild.lastElementChild.innerHTML.toLowerCase().includes(e.innerHTML.toLowerCase().trim())?t.classList.remove("d-none"):t.classList.add("d-none")}),null==document.querySelector(".filter-result.active")&&l.forEach(e=>e.classList.remove("d-none"))})})}let copyRight=document.querySelector("footer .finals .copyright");copyRight.innerHTML=`Copyright Â© ${(new Date).getFullYear()} FoodRoad Food delivery`;